import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SensorData() {
  const [sensorReading, setSensorReading] = useState({});
  const [loading, setLoading] = useState(true);

  const fetchData = () => {
    // Fetch temperature reading data from the Spring Boot API
    axios
      .get('http://localhost:8080/sensors/temperature?sensorId=sensor1')
      .then((response) => {
        setSensorReading(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching sensor data:', error);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchData(); // Fetch data initially

    // Set up an interval to fetch data every 5 seconds (adjust as needed)
    const intervalId = setInterval(() => {
      fetchData();
    }, 500); // 5000 milliseconds = 5 seconds

    // Cleanup the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
      <h2>Sensor Data</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <p>Sensor ID: {sensorReading.sensorId}</p>
          <p>Type: {sensorReading.type}</p>
          <p>Reading: {sensorReading.reading}</p>
        </div>
      )}
    </div>
  );
}

export default SensorData;
